&lt;p&gt;/root/.ssh/id_rsa&lt;/p&gt;
&lt;p&gt;/root/.ssh/id_ras.keystore&lt;/p&gt;
&lt;p&gt;/root/.ssh/known_hosts&lt;/p&gt;
